// Import the functions you need from the SDKs you need
import { initializeApp } from "firebase/app";
import { getAnalytics } from "firebase/analytics";
import 'firebase/auth'; // auth 모듈을 import합니다.
import 'firebase/storage'; // storage 모듈을 import합니다.


// Your web app's Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyCmQuGHuMS-ea-3OthSCeHXXeLFX5IxHGc",
  authDomain: "myapp-3be3f.firebaseapp.com",
  projectId: "myapp-3be3f",
  storageBucket: "myapp-3be3f.appspot.com",
  messagingSenderId: "1054886895758",
  appId: "1:1054886895758:web:6083f5385d043de8b0e9df",
  measurementId: "G-KX7XJQ5RDX"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const analytics = getAnalytics(app);

export const auth = firebase.auth(); // auth를 export합니다.
export const storage = firebase.storage(); // storage를 export합니다.
